import React from 'react';
import { Link, useNavigate } from 'react-router-dom';

const AdminPage = () => {
  const navigate = useNavigate();

  const handleLogout = () => {
    const confirmar = window.confirm('¿Estás seguro de cerrar sesión?');
    if (confirmar) {
      localStorage.removeItem("isAuthenticated");
      navigate("/");
    }
  };

  return (
    <div className="d-flex flex-column min-vh-100">
      {/* ENCABEZADO ADMIN */}
      <header className="bg-esfot text-white py-3 px-4 d-flex justify-content-between align-items-center">
        <img src="/imagenes_asoesfot/logo.png" alt="ESFOT" style={{ height: '60px' }} />
        <div>
          <Link to="/admin/gestionar" className="btn btn-esfot me-2">Gestionar Aportaciones</Link>
          <Link to="/admin/reportes" className="btn btn-esfot me-2">Visualizar Reportes</Link>
          <Link to="/admin/finanzas" className="btn btn-esfot me-2">Finanzas</Link>
          <button onClick={handleLogout} className="btn btn-danger">Cerrar sesión</button>
        </div>
      </header>

      {/* CUERPO PRINCIPAL */}
      <main className="flex-grow-1 container text-center py-5">
        <h1 className="display-4 fw-bold mb-3">ADMINISTRACIÓN ASO-ESFOT</h1>
        <p className="lead mb-5 text-secondary fw-semibold">Panel de control para gestión financiera y administrativa</p>

        <div className="container my-5">
          <div className="row align-items-center mb-4">
            <div className="col-md-6">
              <img src="/imagenes_asoesfot/gestion_aportes.jpg" alt="Gestión" className="img-fluid rounded shadow-sm" />
            </div>
            <div className="col-md-6">
              <p className="lead text-secondary fw-semibold">
                Supervisa y organiza las aportaciones realizadas por los estudiantes. Gestiona planes, fechas, montos y verifica cumplimiento.
              </p>
            </div>
          </div>

          <div className="row align-items-center mb-4">
            <div className="col-md-6 order-md-2">
              <img src="/imagenes_asoesfot/reportes.jpg" alt="Reportes" className="img-fluid rounded shadow-sm" />
            </div>
            <div className="col-md-6 order-md-1">
              <p className="lead text-secondary fw-semibold">
                Accede a reportes detallados de aportes, balances financieros, participación y más, con opciones gráficas e imprimibles.
              </p>
            </div>
          </div>

          <div className="row align-items-center mb-4">
            <div className="col-md-6">
              <img src="/imagenes_asoesfot/finanzas.jpg" alt="Finanzas" className="img-fluid rounded shadow-sm" />
            </div>
            <div className="col-md-6">
              <p className="lead text-secondary fw-semibold">
                Consulta el estado financiero de la asociación. Registra ingresos y egresos, y mantén un control transparente de los fondos.
              </p>
            </div>
          </div>
        </div>
      </main>

      {/* PIE DE PÁGINA */}
      <footer className="bg-esfot text-white text-center py-3">
        &copy; 2025 ASO-ESFOT. Panel administrativo.
      </footer>
    </div>
  );
};

export default AdminPage;
