import React, { useState } from 'react';

const CrudPlanesPage = () => {
  const [planes, setPlanes] = useState([]);
  const [form, setForm] = useState({
    titulo: '',
    beneficios: '',
    imagen: ''
  });
  const [editIndex, setEditIndex] = useState(null);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const beneficiosArray = form.beneficios.split(',').map(item => item.trim());

    if (editIndex !== null) {
      const updatedPlanes = [...planes];
      updatedPlanes[editIndex] = { ...form, beneficios: beneficiosArray };
      setPlanes(updatedPlanes);
      setEditIndex(null);
    } else {
      setPlanes([...planes, { ...form, beneficios: beneficiosArray }]);
    }

    setForm({ titulo: '', beneficios: '', imagen: '' });
  };

  const handleEdit = (index) => {
    const plan = planes[index];
    setForm({
      titulo: plan.titulo,
      beneficios: plan.beneficios.join(', '),
      imagen: plan.imagen
    });
    setEditIndex(index);
  };

  const handleDelete = (index) => {
    const confirm = window.confirm('¿Estás seguro de eliminar este plan?');
    if (confirm) {
      const updatedPlanes = planes.filter((_, i) => i !== index);
      setPlanes(updatedPlanes);
    }
  };

  return (
    <div className="container my-5">
      <h2 className="text-center mb-4">Gestión de Planes de Aportaciones</h2>

      {/* Formulario */}
      <form onSubmit={handleSubmit} className="card p-4 mb-5 shadow-sm">
        <div className="mb-3">
          <label className="form-label">Título del Plan</label>
          <input
            type="text"
            name="titulo"
            className="form-control"
            value={form.titulo}
            onChange={handleChange}
            required
          />
        </div>

        <div className="mb-3">
          <label className="form-label">Beneficios (separados por coma)</label>
          <textarea
            name="beneficios"
            className="form-control"
            value={form.beneficios}
            onChange={handleChange}
            rows={3}
            required
          ></textarea>
        </div>

        <div className="mb-3">
          <label className="form-label">URL de la Imagen</label>
          <input
            type="text"
            name="imagen"
            className="form-control"
            value={form.imagen}
            onChange={handleChange}
            required
          />
        </div>

        <button type="submit" className="btn btn-primary">
          {editIndex !== null ? 'Actualizar Plan' : 'Agregar Plan'}
        </button>
      </form>

      {/* Tabla de Planes */}
      <div className="table-responsive">
        <table className="table table-bordered table-hover align-middle">
          <thead className="table-dark">
            <tr>
              <th>#</th>
              <th>Imagen</th>
              <th>Título</th>
              <th>Beneficios</th>
              <th>Acciones</th>
            </tr>
          </thead>
          <tbody>
            {planes.length === 0 ? (
              <tr>
                <td colSpan="5" className="text-center text-muted">
                  No hay planes registrados.
                </td>
              </tr>
            ) : (
              planes.map((plan, index) => (
                <tr key={index}>
                  <td>{index + 1}</td>
                  <td>
                    <img src={plan.imagen} alt={plan.titulo} style={{ height: '80px' }} />
                  </td>
                  <td>{plan.titulo}</td>
                  <td>
                    <ul>
                      {plan.beneficios.map((item, i) => (
                        <li key={i}>{item}</li>
                      ))}
                    </ul>
                  </td>
                  <td>
                    <button
                      className="btn btn-warning btn-sm me-2"
                      onClick={() => handleEdit(index)}
                    >
                      Editar
                    </button>
                    <button
                      className="btn btn-danger btn-sm"
                      onClick={() => handleDelete(index)}
                    >
                      Eliminar
                    </button>
                  </td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default CrudPlanesPage;
