import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const ReportesPage = () => {
  const [busqueda, setBusqueda] = useState('');
  const [aportantes, setAportantes] = useState([
    { nombre: 'Juan', apellido: 'Pérez', plan: 'Tianlong', fecha: '2025-03-12', estado: 'Pendiente' },
    { nombre: 'Ana', apellido: 'Martínez', plan: 'Celestial', fecha: '2025-03-15', estado: 'Pagado' },
    { nombre: 'Luis', apellido: 'García', plan: 'Colacuerno', fecha: '2025-03-18', estado: 'Pendiente' }
  ]);

  const filtrados = aportantes.filter(ap =>
    `${ap.nombre} ${ap.apellido}`.toLowerCase().includes(busqueda.toLowerCase())
  );

  const generarReporte = () => {
    const reporte = filtrados.map(ap =>
      `${ap.nombre} ${ap.apellido} - ${ap.plan} - ${ap.fecha} - ${ap.estado}`
    ).join('\n');
    const blob = new Blob([reporte], { type: 'text/plain' });
    const url = URL.createObjectURL(blob);
    const link = document.createElement('a');
    link.download = 'reporte_aportantes.txt';
    link.href = url;
    link.click();
  };

  const cambiarEstado = (index) => {
    const nuevosAportantes = [...aportantes];
    nuevosAportantes[index].estado = nuevosAportantes[index].estado === 'Pagado' ? 'Pendiente' : 'Pagado';
    setAportantes(nuevosAportantes);
  };

  return (
    <div className="d-flex flex-column min-vh-100">
      {/* Encabezado */}
      <header className="bg-esfot text-white py-3 px-4 d-flex justify-content-between align-items-center">
        <img src="/imagenes_asoesfot/logo.png" alt="ESFOT" style={{ height: '60px' }} />
        <Link to="/adminpage" className="btn btn-light">Inicio</Link>
      </header>

      {/* Cuerpo */}
      <main className="flex-grow-1 container py-5">
        <h2 className="text-center mb-4">Reportes de Aportaciones</h2>

        {/* Búsqueda */}
        <div className="input-group mb-4">
          <input
            type="text"
            className="form-control"
            placeholder="Buscar por nombre o apellido"
            value={busqueda}
            onChange={(e) => setBusqueda(e.target.value)}
          />
          <button className="btn btn-danger" onClick={generarReporte}>
            Generar Reporte
          </button>
        </div>

        {/* Tabla */}
        <div className="table-responsive">
          <table className="table table-bordered table-hover">
            <thead className="table-dark">
              <tr>
                <th>#</th>
                <th>Nombre</th>
                <th>Apellido</th>
                <th>Plan</th>
                <th>Fecha de Registro</th>
                <th>Estado</th>
              </tr>
            </thead>
            <tbody>
              {filtrados.length > 0 ? (
                filtrados.map((ap, i) => {
                  const indexOriginal = aportantes.findIndex(
                    a => a.nombre === ap.nombre && a.apellido === ap.apellido
                  );
                  return (
                    <tr key={i}>
                      <td>{i + 1}</td>
                      <td>{ap.nombre}</td>
                      <td>{ap.apellido}</td>
                      <td>{ap.plan}</td>
                      <td>{ap.fecha}</td>
                      <td>
                        <span className={`badge ${ap.estado === 'Pagado' ? 'bg-success' : 'bg-warning text-dark'}`}>
                          {ap.estado}
                        </span>
                        <button
                          className="btn btn-sm btn-outline-secondary ms-2"
                          onClick={() => cambiarEstado(indexOriginal)}
                        >
                          Cambiar
                        </button>
                      </td>
                    </tr>
                  );
                })
              ) : (
                <tr>
                  <td colSpan="6" className="text-center text-muted">No se encontraron coincidencias.</td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </main>

      {/* Pie de página */}
      <footer className="bg-esfot text-white text-center py-3">
        &copy; 2025 ASO-ESFOT. Todos los derechos reservados.
      </footer>
    </div>
  );
};

export default ReportesPage;
