import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const CrudPlanesPage = () => {
  const [planes, setPlanes] = useState([]);
  const [form, setForm] = useState({
    titulo: '',
    beneficios: '',
    imagen: null,
    imagenPreview: ''
  });
  const [editIndex, setEditIndex] = useState(null);

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    if (name === 'imagen') {
      const file = files[0];
      if (file) {
        const preview = URL.createObjectURL(file);
        setForm(prev => ({ ...prev, imagen: file, imagenPreview: preview }));
      }
    } else {
      setForm(prev => ({ ...prev, [name]: value }));
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const beneficiosArray = form.beneficios.split(',').map(item => item.trim());

    const nuevoPlan = {
      titulo: form.titulo,
      beneficios: beneficiosArray,
      imagenPreview: form.imagenPreview,
      imagenFile: form.imagen // puedes usarlo si más adelante subes a Firebase Storage
    };

    if (editIndex !== null) {
      const updatedPlanes = [...planes];
      updatedPlanes[editIndex] = nuevoPlan;
      setPlanes(updatedPlanes);
      setEditIndex(null);
    } else {
      setPlanes([...planes, nuevoPlan]);
    }

    setForm({ titulo: '', beneficios: '', imagen: null, imagenPreview: '' });
  };

  const handleEdit = (index) => {
    const plan = planes[index];
    setForm({
      titulo: plan.titulo,
      beneficios: plan.beneficios.join(', '),
      imagen: plan.imagenFile,
      imagenPreview: plan.imagenPreview
    });
    setEditIndex(index);
  };

  const handleDelete = (index) => {
    const confirm = window.confirm('¿Estás seguro de eliminar este plan?');
    if (confirm) {
      const updatedPlanes = planes.filter((_, i) => i !== index);
      setPlanes(updatedPlanes);
    }
  };

  return (
    <div className="d-flex flex-column min-vh-100">
      {/* Encabezado */}
      <header className="bg-esfot text-white py-3 px-4 d-flex justify-content-between align-items-center">
        <img src="/imagenes_asoesfot/logo.png" alt="ESFOT" style={{ height: '60px' }} />
        <Link to="/adminpage" className="btn btn-light">Inicio</Link>
      </header>

      {/* Cuerpo */}
      <main className="flex-grow-1 container py-5">
        <h2 className="text-center mb-4">Gestión de Planes de Aportaciones</h2>

        <form onSubmit={handleSubmit} className="card p-4 mb-5 shadow-sm">
          <div className="mb-3">
            <label className="form-label">Título del Plan</label>
            <input
              type="text"
              name="titulo"
              className="form-control"
              value={form.titulo}
              onChange={handleChange}
              required
            />
          </div>

          <div className="mb-3">
            <label className="form-label">Beneficios (separados por coma)</label>
            <textarea
              name="beneficios"
              className="form-control"
              value={form.beneficios}
              onChange={handleChange}
              rows={3}
              required
            ></textarea>
          </div>

          <div className="mb-3">
            <label className="form-label">Imagen del Plan</label>
            <input
              type="file"
              name="imagen"
              accept="image/*"
              className="form-control"
              onChange={handleChange}
              required={editIndex === null} // solo requerida en nuevo
            />
            {form.imagenPreview && (
              <img
                src={form.imagenPreview}
                alt="Vista previa"
                style={{ height: '100px', marginTop: '10px' }}
              />
            )}
          </div>

          <button type="submit" className="btn btn-danger">
            {editIndex !== null ? 'Actualizar Plan' : 'Agregar Plan'}
          </button>
        </form>

        {/* Tabla */}
        <div className="table-responsive">
          <table className="table table-bordered table-hover align-middle">
            <thead className="table-dark">
              <tr>
                <th>#</th>
                <th>Imagen</th>
                <th>Título</th>
                <th>Beneficios</th>
                <th>Acciones</th>
              </tr>
            </thead>
            <tbody>
              {planes.length === 0 ? (
                <tr>
                  <td colSpan="5" className="text-center text-muted">
                    No hay planes registrados.
                  </td>
                </tr>
              ) : (
                planes.map((plan, index) => (
                  <tr key={index}>
                    <td>{index + 1}</td>
                    <td>
                      <img
                        src={plan.imagenPreview}
                        alt={plan.titulo}
                        style={{ height: '80px' }}
                      />
                    </td>
                    <td>{plan.titulo}</td>
                    <td>
                      <ul>
                        {plan.beneficios.map((item, i) => (
                          <li key={i}>{item}</li>
                        ))}
                      </ul>
                    </td>
                    <td>
                      <button
                        className="btn btn-warning btn-sm me-2"
                        onClick={() => handleEdit(index)}
                      >
                        Editar
                      </button>
                      <button
                        className="btn btn-danger btn-sm"
                        onClick={() => handleDelete(index)}
                      >
                        Eliminar
                      </button>
                    </td>
                  </tr>
                ))
              )}
            </tbody>
          </table>
        </div>
      </main>

      {/* Footer */}
      <footer className="bg-esfot text-white text-center py-3">
        &copy; 2025 ASO-ESFOT. Todos los derechos reservados.
      </footer>
    </div>
  );
};

export default CrudPlanesPage;
