import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const FinanzasPage = () => {
  const [ingresos, setIngresos] = useState([]);
  const [gastos, setGastos] = useState([]);
  const [formIngreso, setFormIngreso] = useState({ monto: '', descripcion: '' });
  const [formGasto, setFormGasto] = useState({ monto: '', descripcion: '' });

  const totalIngresos = ingresos.reduce((acc, item) => acc + parseFloat(item.monto), 0);
  const totalGastos = gastos.reduce((acc, item) => acc + parseFloat(item.monto), 0);
  const saldo = totalIngresos - totalGastos;

  const generarArchivoTxt = (tipo, monto, descripcion, fecha) => {
    const contenido =
      `--- Registro de ${tipo} ---\n` +
      `Monto: $${monto}\n` +
      `Descripción: ${descripcion}\n` +
      `Fecha: ${fecha}\n` +
      `Saldo actual: $${(tipo === 'Ingreso' ? saldo + parseFloat(monto) : saldo - parseFloat(monto)).toFixed(2)}\n\n`;

    const blob = new Blob([contenido], { type: 'text/plain;charset=utf-8' });
    const enlace = document.createElement('a');
    enlace.href = URL.createObjectURL(blob);
    enlace.download = `${tipo}_${fecha.replace(/[\/:]/g, '-')}.txt`;
    enlace.click();
  };

  const handleChangeIngreso = (e) => {
    setFormIngreso({ ...formIngreso, [e.target.name]: e.target.value });
  };

  const handleChangeGasto = (e) => {
    setFormGasto({ ...formGasto, [e.target.name]: e.target.value });
  };

  const handleAddIngreso = (e) => {
    e.preventDefault();
    const fecha = new Date().toLocaleString();
    const nuevoIngreso = { ...formIngreso, fecha };
    setIngresos([...ingresos, nuevoIngreso]);
    generarArchivoTxt('Ingreso', formIngreso.monto, formIngreso.descripcion, fecha);
    setFormIngreso({ monto: '', descripcion: '' });
  };

  const handleAddGasto = (e) => {
    e.preventDefault();
    const fecha = new Date().toLocaleString();
    const nuevoGasto = { ...formGasto, fecha };
    setGastos([...gastos, nuevoGasto]);
    generarArchivoTxt('Gasto', formGasto.monto, formGasto.descripcion, fecha);
    setFormGasto({ monto: '', descripcion: '' });
  };

  return (
    <div className="d-flex flex-column min-vh-100">
      <header className="bg-esfot text-white py-3 px-4 d-flex justify-content-between align-items-center">
        <img src="/imagenes_asoesfot/logo.png" alt="Logo" style={{ height: '60px' }} />
        <Link to="/adminpage" className="btn btn-light">Inicio</Link>
      </header>

      <main className="flex-grow-1 container py-4">
        <h2 className="text-center mb-4">Gestión Financiera - ASO ESFOT</h2>

        <div className="alert alert-info text-center fs-5 fw-bold">
          Saldo Total: ${saldo.toFixed(2)}
        </div>

        <div className="card mb-4 p-3 shadow-sm">
          <h5>Registrar Aportación</h5>
          <form onSubmit={handleAddIngreso}>
            <div className="mb-2">
              <input
                type="number"
                name="monto"
                className="form-control"
                placeholder="Monto"
                value={formIngreso.monto}
                onChange={handleChangeIngreso}
                required
              />
            </div>
            <div className="mb-2">
              <input
                type="text"
                name="descripcion"
                className="form-control"
                placeholder="Descripción"
                value={formIngreso.descripcion}
                onChange={handleChangeIngreso}
                required
              />
            </div>
            <button className="btn btn-success">Agregar Aportación</button>
          </form>
        </div>

        <div className="card mb-4 p-3 shadow-sm">
          <h5>Registrar Gasto</h5>
          <form onSubmit={handleAddGasto}>
            <div className="mb-2">
              <input
                type="number"
                name="monto"
                className="form-control"
                placeholder="Monto del gasto"
                value={formGasto.monto}
                onChange={handleChangeGasto}
                required
              />
            </div>
            <div className="mb-2">
              <input
                type="text"
                name="descripcion"
                className="form-control"
                placeholder="Descripción del gasto"
                value={formGasto.descripcion}
                onChange={handleChangeGasto}
                required
              />
            </div>
            <button className="btn btn-danger">Registrar Gasto</button>
          </form>
        </div>

        <div className="row">
          <div className="col-md-6">
            <h5>Reporte de Ingresos</h5>
            <ul className="list-group">
              {ingresos.map((item, index) => (
                <li key={index} className="list-group-item">
                  <strong>${item.monto}</strong> - {item.descripcion} <br />
                  <small className="text-muted">{item.fecha}</small>
                </li>
              ))}
            </ul>
          </div>

          <div className="col-md-6">
            <h5>Reporte de Gastos</h5>
            <ul className="list-group">
              {gastos.map((item, index) => (
                <li key={index} className="list-group-item">
                  <strong>${item.monto}</strong> - {item.descripcion} <br />
                  <small className="text-muted">{item.fecha}</small>
                </li>
              ))}
            </ul>
          </div>
        </div>
      </main>

      <footer className="bg-esfot text-white text-center py-3">
        &copy; 2025 ASO-ESFOT. Todos los derechos reservados.
      </footer>
    </div>
  );
};

export default FinanzasPage;
