import React, { useState } from 'react';

const RegistroPage = () => {
  const [showInfo, setShowInfo] = useState([false, false, false]);

  const toggleInfo = (index) => {
    const updated = [...showInfo];
    updated[index] = !updated[index];
    setShowInfo(updated);
  };

  const sections = [
    {
      image: '/imagenes_asoesfot/img1.jpg',
      title: 'Sección 1',
      info: 'Esta es la información detallada de la Sección 1.',
    },
    {
      image: '/imagenes_asoesfot/img2.jpg',
      title: 'Sección 2',
      info: 'Aquí va el contenido adicional de la Sección 2.',
    },
    {
      image: '/imagenes_asoesfot/img3.jpg',
      title: 'Sección 3',
      info: 'Detalles y descripción extendida de la Sección 3.',
    },
  ];

  return (
    <div className="container py-5">
      <h1 className="text-center mb-4">Página de Registro</h1>
      <div className="row">
        {sections.map((section, index) => (
          <div className="col-md-4 mb-4" key={index}>
            <div className="card h-100">
              <img src={section.image} className="card-img-top" alt={section.title} />
              <div className="card-body text-center">
                <button
                  className="btn btn-danger"
                  onClick={() => toggleInfo(index)}
                >
                  {showInfo[index] ? 'Ocultar' : 'Ver más'}
                </button>
                {showInfo[index] && (
                  <div className="mt-3 text-start">
                    <p>{section.info}</p>
                  </div>
                )}
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default RegistroPage;
