import React from 'react';

const VisualizarPage = () => {
  // Simulación de datos (puedes reemplazar con datos reales de Firebase, por ejemplo)
  const aportaciones = [
    { fecha: '2025-05-15', plan: 'Plan Colacuerno', estado: 'Pagado' },
    { fecha: '2025-03-20', plan: 'Plan Tianlong', estado: 'Pendiente' },
  ];

  return (
    <div className="d-flex flex-column min-vh-100">
      {/* ENCABEZADO */}
      <header className="bg-esfot text-white py-3 px-4 d-flex justify-content-between align-items-center">
        <img src="/imagenes_asoesfot/logo.png" alt="ESFOT" style={{ height: '60px' }} />
      </header>
      {/* CUERPO */}
      <main className="flex-grow-1 container py-5">
        <h2 className="text-center mb-4">Historial de Aportaciones</h2>
        {aportaciones.length > 0 ? (
          <table className="table table-striped shadow">
            <thead className="table-dark">
              <tr>
                <th>Fecha</th>
                <th>Plan</th>
                <th>Estado</th>
              </tr>
            </thead>
            <tbody>
              {aportaciones.map((item, index) => (
                <tr key={index}>
                  <td>{item.fecha}</td>
                  <td>{item.plan}</td>
                  <td>{item.estado}</td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <p className="text-center text-muted">No tienes aportaciones registradas.</p>
        )}
      </main>

      {/* PIE DE PÁGINA */}
      <footer className="bg-esfot text-white text-center py-3">
        &copy; 2025 ASO-ESFOT. Todos los derechos reservados.
      </footer>
    </div>
  );
};

export default VisualizarPage;
