import React, { useState } from 'react';

const RegistroPage = () => {
  const [visible, setVisible] = useState([false, false, false]);

  const toggle = (index) => {
    const updated = [...visible];
    updated[index] = !updated[index];
    setVisible(updated);
  };

  const elegirPlan = (nombrePlan) => {
    const confirmar = window.confirm(`¿Estás seguro de elegir el ${nombrePlan}?`);
    if (confirmar) {
      alert(`Has seleccionado el ${nombrePlan}.`);
      // Aquí podrías guardar en Firebase o redirigir al usuario
    }
  };

  const secciones = [
    {
      imagen: '/imagenes_asoesfot/tianlong.jpeg',
      titulo: 'Plan Tianlong',
      lista: [
        '4 horas de billar',
        '2 horas en consolas',
        'Stickers',
        'Descuento en casilleros',
        'Colada morada con guagua',
      ],
    },
    {
      imagen: '/imagenes_asoesfot/colacuerno.jpg',
      titulo: 'Plan Colacuerno',
      lista: [
        '8 horas de billar',
        '6 horas en consolas',
        'Stickers',
        '50% en casilleros',
        'Termo',
        '1 almuerzo',
        'Gorra',
        'Descuento del 20% en chompas o camisetas',
        'Descuento en cursos ESFOT',
        'Funda de caramelos',
        'Colada morada con guagua',
        'Carnet especial',
        'Canelazo',
      ],
    },
    {
      imagen: '/imagenes_asoesfot/celestial.jpeg',
      titulo: 'Plan Celestial',
      lista: [
        '10 horas de billar en el semestre',
        '10 horas en consolas',
        'Stickers',
        'Pin',
        'Casillero gratis',
        'Termo',
        '3 almuerzos en el semestre',
        'Gorra',
        'Camiseta',
        'Descuento del 35% en chompas o camisetas',
        'Descuento en cursos ESFOT',
        'Descuento en tienda',
        'Funda de caramelos',
        'Tarrina de colada morada con su guagua',
        'Regalo sorpresa en Navidad',
        'Entrada a fiestas ESFOT',
        'Carnet especial',
        'Canelazo',
      ],
    },
  ];

  return (
    <div className="d-flex flex-column min-vh-100">
      {/* ENCABEZADO */}
      <header className="bg-esfot text-white py-3 px-4 d-flex justify-content-between align-items-center">
        <img src="/imagenes_asoesfot/logo.png" alt="ESFOT" style={{ height: '60px' }} />
      </header>

      {/* CUERPO PRINCIPAL */}
      <main className="flex-grow-1 d-flex flex-column align-items-center justify-content-center p-4">
        <h2 className="mb-4 text-center">Planes de Aportaciones</h2>
        <p className="lead text-secondary fw-semibold">
        Empleando la modernizacion de la Asociacion
        de Estudiantes de la ESFOT, modernizando sus instalaciones ademas de mejorar la infraestructura
        y los servicios que ofrece a los estudiantes.👷‍♂️
      </p>
        <div className="container">
          <div className="row">
            {secciones.map((item, index) => (
              <div className="col-md-4 mb-4" key={index}>
                <div className="card h-100">
                  <img src={item.imagen} className="card-img-top" alt={`Imagen ${index + 1}`} />
                  <div className="card-body text-center">
                    <button className="btn btn-danger mb-2" onClick={() => toggle(index)}>
                      {visible[index] ? 'Ocultar' : 'Ver más'}
                    </button>
                    {visible[index] && (
                      <div className="mt-3 text-start">
                        <p><strong>{item.titulo}:</strong></p>
                        <ul>
                          {item.lista.map((detalle, i) => (
                            <li key={i}>{detalle}</li>
                          ))}
                        </ul>
                      </div>
                    )}
                    {/* Botón Elegir Plan */}
                    <button
                      className="btn btn-primary mt-3"
                      onClick={() => elegirPlan(item.titulo)}
                    >
                      Elegir plan
                    </button>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </main>

      {/* PIE DE PÁGINA */}
      <footer className="bg-esfot text-white text-center py-3">
        &copy; 2025 ASO-ESFOT. Todos los derechos reservados.
      </footer>
    </div>
  );
};

export default RegistroPage;
